def get_random_tags() -> list[str]:
    based = random.sample(tags_based, random.randrange(30))
    inst = random.sample(tags_inst, random.randrange(50))
    return based + inst


async def update_tags_by_user_id(
        tags: list[str],
        user_id: uuid.UUID,
        session: AsyncSession
):
    user = await session.get(User, user_id)
    return await helper_update_user_tags(tags, user, session)


async def update_tags_by_chat_id(
        tags: list[str],
        chat_id: uuid.UUID,
        session: AsyncSession
):
    chat = await session.get(Chat, chat_id)
    return await helper_update_chat_tags(tags, chat, session)


async def seed_users_tags(session: AsyncSession):
    for user in users:
        tags = get_random_tags()
        await update_tags_by_user_id(tags, user['id'], session)
